import requests
import shutil

def dlPng(urlSet, pkSet):
    for i in range(len(urlSet)):
        r = requests.get(urlSet[i], stream=True, headers={'User-agent': 'Mozilla/5.0'})
        print(r.status_code)
        if r.status_code == 200:
            with open(str(pkSet[i]) + ".gif", 'wb') as f:
                r.raw.decode_content = True
                shutil.copyfileobj(r.raw, f)
        print("done with " + str(i))

def makeUrlSet(set):
    urlSet= []
    for i in range(len(set)):
        url= "https://img.pokemondb.net/sprites/black-white/anim/normal/" + set[i] + ".gif"
        urlSet.append(url)
    return urlSet

def makeSet(text):
    n= False
    i= 0
    res= []
    name= []
    while n == False:
        if i == len(text): return res
        look= text[i]
        if look.isupper() and i != 0:
            aname= ""
            for el in name:
                aname+= str(el)
            res.append(aname)
            name = []
            name.append(look.lower())
        else:
            name.append(look)
        i+=1

def refine(text):
    for el in text:
        for st in el:
            if st == " ":
                el.replace(" ", "-")
            if st.isupper():
                st= st.lower()
            if st == ".":
                el.replace(".", "")
    return text

